"use client";

import React from "react";
import { useRef } from "react";
import { AnimatedBeam } from "@/components/ui/magic-ui/AnimatedBeam";

const Circle = ({ className, children, ref }) => {
  return (
    <div
      ref={ref}
      className={`z-10 flex size-12 items-center justify-center rounded-full border border-white/20 p-3 backdrop-blur-2xl dark:border-black/20 ${className}`}
    >
      {children}
    </div>
  );
};

Circle.displayName = "Circle";

export default function AnimatedBeamDemo() {
  const containerRef = useRef(null);
  const div1Ref = useRef(null);
  const div2Ref = useRef(null);
  const div3Ref = useRef(null);
  const div4Ref = useRef(null);
  const div5Ref = useRef(null);
  const div6Ref = useRef(null);
  const div7Ref = useRef(null);

  return (
    <div
      className="relative mx-auto flex h-full w-full items-center justify-center overflow-hidden p-10"
      ref={containerRef}
    >
      <div className="flex size-full max-w-lg flex-col items-stretch justify-between gap-10">
        <div className="absolute inset-0 -z-10 h-full w-full items-center px-5 py-24 opacity-[0.1] [background:radial-gradient(125%_100%_at_50%_10%,#1e1e1e_40%,#72b894_100%)] dark:[background:radial-gradient(125%_100%_at_50%_10%,#fff_40%,#72b894_100%)]"></div>
        <div className="flex flex-row items-center justify-between">
          <Circle ref={div1Ref}>
            <Icons.NextJs />
          </Circle>
          <Circle ref={div5Ref}>
            <Icons.gemini />
          </Circle>
        </div>
        <div className="flex flex-row items-center justify-between">
          <Circle ref={div2Ref}>
            <Icons.motion />
          </Circle>
          <Circle ref={div4Ref} className="size-16">
            <Icons.popsy />
          </Circle>
          <Circle ref={div6Ref}>
            <Icons.css />
          </Circle>
        </div>
        <div className="flex flex-row items-center justify-between">
          <Circle ref={div3Ref}>
            <Icons.tailwindCss />
          </Circle>
          <Circle ref={div7Ref}>
            <Icons.react />
          </Circle>
        </div>
      </div>

      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div1Ref}
        toRef={div4Ref}
        curvature={-75}
        endYOffset={-10}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div2Ref}
        toRef={div4Ref}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div3Ref}
        toRef={div4Ref}
        curvature={75}
        endYOffset={10}
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div5Ref}
        toRef={div4Ref}
        curvature={-75}
        endYOffset={-10}
        reverse
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div6Ref}
        toRef={div4Ref}
        reverse
      />
      <AnimatedBeam
        containerRef={containerRef}
        fromRef={div7Ref}
        toRef={div4Ref}
        curvature={75}
        endYOffset={10}
        reverse
      />
    </div>
  );
}

